import React, { Component } from 'react';
import { OffCanvas, OffCanvasMenu, OffCanvasBody } from 'react-offcanvas';
import { Label,Menu,Grid,Header,Icon,Dropdown,Modal,Form,Button,Input } from 'semantic-ui-react';

export default class Drawer extends Component {



  render() {
    return (
      <Menu size='massive' style={{marginTop: -436,marginLeft: 950,height:600}} vertical>
        <Icon close />
        <Menu.Item style={{marginTop:100}} name='inbox'  >
          <Label color='teal'>1</Label>
            Inbox
        </Menu.Item>

        <Menu.Item name='spam' >
          <Label>51</Label>
            Spam
        </Menu.Item>

        <Menu.Item name='updates' a >
          <Label>1</Label>
            Updates
        </Menu.Item>
        <Menu.Item>
          <Input icon='search' placeholder='Search mail...' />
        </Menu.Item>
      </Menu>
        )
      }
  }
