import React, { Component } from 'react';
import UserPanel from './UserPanel';
import { Menu } from 'semantic-ui-react';
import firebase from 'firebase';
import { connect } from 'react-redux';


class SideBar extends Component {
  constructor(props){
    super(props);
    this.state={
      users:[],
      user:''
    }
    this.userRef = firebase.database().ref('users');
  }

  componentWillReceiveProps(nextProps){
    this.setState({user:nextProps.currentUser})
  }

  componentDidMount(){
    this.listenUsers()
  }

  listenUsers() {
    let users=[]
    this.userRef
      .on('value',snapshot => {
        snapshot.forEach(snap => {
          users.push(snap.val())
        })
        this.setState({users:Object.values(snapshot.val())})
      })
    }

    listUser = users => {
      const userInList = this.state.users.includes(this.state.user && this.props.currentUser.displayName)

      if(userInList){
        return null;
      } else{
        return users.map((user,key) => {
            return(
            <div key={user.name}>
              {user.name}
            </div>
          )
          })
        }
      }





  render() {
    console.log(this.state.user && this.props.currentUser.displayName)
    const users = this.state.users
    return (
      <Menu style={{background:'#4d394b'}}  size="small" fixed="left" vertical>
        <UserPanel signOut={this.props.signOut} />
        <div style={{fontSize:"20px",marginLeft:"20px",marginTop:"50px"}}>
          {this.listUser(users)}
        </div>
      </Menu>
    );
  }
}

function mapStateToProps(state){
  return{
  currentUser:state.user.currentUser
  }
}


export default connect(mapStateToProps)(SideBar);
