import React, { Component } from 'react';
import { Label,Menu,Grid,Header,Icon,Dropdown,Modal,Divider} from 'semantic-ui-react';
import { connect } from 'react-redux';
import * as actions from '../actions';

import {SlideDown} from 'react-slidedown'
import '../../node_modules/react-slidedown/lib/slidedown.css'

const {CSSTransitionGroup} = React.addons.CSSTransitionGroup;

class Drawer extends Component {

  state={isShown:true}

  handleClick = () => {
    this.setState({isShown:false})
  }

  slideBox(){
    return (
      <div id="box" key="key">
        {this.props.text}
      </div>
    )
  }

  render() {
    const text = `Lorem ipsum dolor sit amet, consectetur adipisicing elit. Accusantium architecto deleniti dolor doloribus enim hic illo iste non, numquam quas quod repellat reprehenderit rerum sunt, totam vel vero voluptates! Adipisci commodi distinctio eos esse, est harum impedit in quis similique, tenetur unde, vero. Atque dignissimos eaque esse ex, fuga hic id ipsam mollitia, odit officia perferendis quos ratione repudiandae sed suscipit tenetur vero voluptas voluptatibus. Asperiores blanditiis eos esse explicabo fuga illo iure libero molestias pariatur quia quibusdam quis sequi totam vel, voluptas. Aliquam beatae dolor ducimus in, laborum laudantium magnam quae quasi quia, quo, quos soluta tempora tempore totam.`;


  let btnText = this.state.isShown ? 'hide' : 'show';
  let component = this.state.isShown ? this.SlideBox() : '';

    return (
      <Menu size='massive' style={{marginTop: -436,marginLeft: 950,height:600}} vertical>
      <Menu.Item  style={{height:50,marginTop:10}}>
        <Icon onClick={this.props.closeDrawer} size="large" name="close" style={{marginTop:-10,marginRight:20}} />
        About
      </Menu.Item>
      <Divider />
      <Menu.Item onClick={this.handleClick} style={{height:50,marginTop:10}} name='inbox'  >
            Channel Details <Icon name="angle right" />
            <CSSTransitionGroup
            transitionName="slide"
            transitionAppear={true}
            transitionAppearTimeout={500}
            transitionEnterTimeout={300}
            transitionLeaveTimeout={300}
          >
            {component}
          </CSSTransitionGroup>
      </Menu.Item>
      <Divider />
      <Menu.Item style={{height:50,marginTop:10}} name='spam' >
            Highlights <Icon name="angle right" />
      </Menu.Item>
      <Divider />
      <Menu.Item style={{height:50,marginTop:10}} name='updates' a >
            Members <Icon name="angle right" />
        </Menu.Item>
      </Menu>
      );
    };
  }

export default connect(null,actions)(Drawer);
