import React, { Component } from 'react';
import { Label,Menu,Grid,Header,Icon,Dropdown,Modal,Form,Button,Input } from 'semantic-ui-react';
import { connect } from 'react-redux';
import * as actions from '../actions';

class Drawer extends Component {

  state = { activeItem: 'inbox' }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })


  render() {
     const { activeItem } = this.state

    return (
      <Menu size='massive' style={{marginTop: -436,marginLeft: 950,height:600}} vertical>
      <Menu.Item active={activeItem === 'inbox'} onClick={this.handleItemClick} style={{height:50,marginTop:20}}>
        <Icon onClick={this.props.closeDrawer} size="large" name="close" style={{marginRight:20}} />
        About
      </Menu.Item>
      <Menu.Item active={activeItem === 'spam'} onClick={this.handleItemClick} style={{height:50,marginTop:20}} name='inbox'  >
          <Label color='teal'>1</Label>
            Channel Details
        </Menu.Item>

        <Menu.Item active={activeItem === 'spam'} onClick={this.handleItemClick} name='spam' >
          <Label>51</Label>
            Highlights
        </Menu.Item>

        <Menu.Item name='updates' a >
          <Label>1</Label>
            Members
        </Menu.Item>
        <Menu.Item>
          <Input icon='search' placeholder='Search mail...' />
        </Menu.Item>
      </Menu>
      );
    };
  }

export default connect(null,actions)(Drawer);
