import React, {Component} from 'react';

export default class Message extends Component {
  render() {
    return (
      <div className="message" >
        <div className="message__author" style={{color:'red'}}>
          <strong>{this.props.userName}</strong>
        </div>

          {this.props.message}
      </div>
    )
  }
}
